    name: '',
    lat: '',
    lon: '',
    error: 'sorry something went wrong!!!',
    errorFlag: false,
    openMap: false
  }
}

function = (n,l,ln) => {
  console.log(n,this.state.lat,this.state.lon)
  this.setState({
    name: n,
    lat:l,
    lon: ln
  })

}




  render(){
    return(
      <div>
        <h1>welcome to the website</h1>
      <Forms func={this.function} />
      <Cards 
      name= {this.state.name}
